var Masonry={init:function(){var n;(n=$(".masonry-grid")).imagesLoaded(function(){n.masonry({itemSelector:".masonry-grid-item",columnWidth:".masonry-grid-sizer",percentPosition:!0})}),$.fn.masonryImagesReveal=function(n){var e=this.data("masonry"),o=e.options.itemSelector;return n.hide(),this.append(n),n.imagesLoaded().progress(function(n,i){i=$(i.img).parents(o);i.show(),e.appended(i)}),this}}};$(document).ready(function(){Masonry.init()});